import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import * as firebase from 'firebase';
import { firebaseConfig } from './config';

firebase.initializeApp(firebaseConfig);

class App extends Component {
  constructor() {
    super();
    this.state = {
      checkedIn: '',
      name: '',
      idNumber: '',
      query: '0'
    };
  }

  componentWillUpdate(newProps, newState) {
    if (this.state.query !== newState.query) {
      const dbRef = firebase.database().ref('students');
      let valueRef = dbRef.child(newState.query);
      valueRef.on('value', snap => {
        this.setState({
          checkedIn: snap.child('checkedIn').val(),
          name: snap.child('name').val(),
          idNumber: snap.child('idNumber').val()
        })
      });
    }
  }

  handleOnSubmit = (event) => {
    event.preventDefault();
    this.setState ({
      query: document.getElementById('id').value
    });
  }

  render () {
    return (
      <div>
        <form onSubmit={this.handleOnSubmit}>
            <input type='text' id='id'/>
            <input type='submit' value='Submit'/>
        </form>
        <h1>The name is: {this.state.name}</h1>
        <h1>The id number is: {this.state.idNumber}</h1>
      </div>
    )
  }
}

export default App;
