import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import * as firebase from 'firebase';
import { firebaseConfig } from './config';

firebase.initializeApp(firebaseConfig);

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: ''
    };
  }

  componentDidMount() {
    const dbRef = firebase.database().ref('students').child('2');
    const valueRef = dbRef.child('name');
    valueRef.on('value', snap => {
      this.setState({
        value: snap.val()
      })
    });
  }

  render () {
    return (
      <div className='App'>
        <h1>The value is: {this.state.value}</h1>
      </div>
    )
  }
}

export default App;
